import { HttpClient } from '@angular/common/http';
import { Inject, Injectable } from '@angular/core';
import { Category, Contact } from '../contact';

@Injectable({
  providedIn: 'root'
})
export class ContactServiceService {
  url: string = "";
  list: Contact[] = [];


  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.url = baseUrl;
  }

  getContacts() {
    this.http.get((this.url + 'contact')).subscribe((data: any) => {
      this.list = data;
      console.log(data);
    });
  }

  getCategories() {
    this.http.get((this.url + 'contact/category')).subscribe((data: any) => {
      return data;
    });
  }

  getSubcategories() {
    this.http.get((this.url + 'contact/subcategory')).subscribe((data: any) => {
      this.category = data;
      console.log(data);
    });
  }
}
