import { HttpClient } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Category, Contact, Subcategory } from '../contact';

@Component({
  selector: 'app-contact-detail',
  templateUrl: './contact-detail.component.html',
  styleUrls: ['./contact-detail.component.css']
})
export class ContactDetailComponent implements OnInit {
  contact!: Contact;
  categories: Category[] = [];
  subcategories: Subcategory[] = [];

  constructor(public http: HttpClient, @Inject('BASE_URL') public baseUrl: string, private route: ActivatedRoute) {
  }

  ngOnInit(): void {
    let id = this.route.snapshot.paramMap.get('id');

    this.http.get<Category[]>(this.baseUrl + 'contact/category').subscribe(result => {
      this.categories = result;
    }, error => console.error(error));

    this.http.get<Contact>((this.baseUrl + 'contact/'+id)).subscribe(result => {
      this.contact = result;
    }, error => console.error(error));

  }

  onPost() {

  }



}
